package com.github.peggybrown.speechrank;import ratpack.handling.Context;import ratpack.server.RatpackServer;import static com.github.peggybrown.speechrank.ConferencesProvider.getConference;import static com.github.peggybrown.speechrank.ConferencesProvider.getConferences;//@Datapublic class Main {    public static void main(String... args) throws Exception {        RatpackServer.start(server -> server                .handlers(chain -> chain                        .get(ctx -> ctx.render("Conferences"))                        .get("conferences", ctx -> ctx.render(getConferences()))                        .get("conference/:name", ctx -> {                            ctx.render(getConference(ctx.getPathTokens().get("name")));                        })));    }    private static void getRender(Context ctx, String object) {        ctx.render(object);    }}