package com.github.peggybrown.speechrank;import ratpack.handling.Context;import ratpack.server.RatpackServer;public class Main {    public static void main(String... args) throws Exception {        RatpackServer.start(server -> server                .handlers(chain -> chain                        .get("conferences",ctx -> ctx.render(ConferencesProvider.getConferences()))                        .get("conference/:name", ctx -> ctx.render(ConferencesProvider.getConference(ctx.getPathTokens().get("name"))))));    }    private static void getRender(Context ctx, String object) {        ctx.render(object);    }}